    {
        //fills data in the "Viewing" pane
        populateViewingFields(currentDebateNumber);
        //fills data in the "Searching" pane, ie. the JTable
        try
        {
            searchTable.setModel(new DebateTableModel());
        }
        catch(IOException e)
        {
            JOptionPane.showMessageDialog(null, "There was an error retrieving "
                    + "your data.  Please try again");
        }
        //setting up navigation buttons
        previousDebateButton.setEnabled(false);
        firstDebateButton.setEnabled(false);
        if(debateList.size() < 2)
        {
            nextDebateButton.setEnabled(false);
            lastDebateButton.setEnabled(false);
        }
        debateTableModel = (DebateTableModel)searchTable.getModel();
        
        //for the "Scoring" pane, the date is set to the current date
        Calendar calendar = Calendar.getInstance();
        yearSelector.setSelectedIndex(calendar.get(Calendar.YEAR) - 
                                      Integer.decode(
                                      yearSelector.getSelectedItem() + ""));
        monthSelector.setSelectedIndex(calendar.get(
                                       Calendar.MONTH) - 
                                       Integer.decode(
                                       monthSelector.getSelectedItem() + ""));
        daySelector.setSelectedIndex(calendar.get(
                                     Calendar.DAY_OF_MONTH) - 
                                     Integer.decode(
                                     daySelector.getSelectedItem() + ""));
        //by default there are 8 debaters in a debate
        numberOfDebatersSelector.setSelectedIndex(7);
        numberOfDebatersSelector2.setSelectedIndex(7);
        
        numberOfResults.setText(debateTableModel.getRowCount() + " "
                + "search results");
    }

