    /**
     * Deletes a debate session.  This is a permanent decision that
     * will be immediately reflected in the text file.  A confirmation
     * dialog box is shown to make sure the user is certain about
     * this decision.
     * @param evt 
     */
    private void deleteButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_deleteButtonActionPerformed
        if(debateList.isEmpty())
        {
            JOptionPane.showMessageDialog(null, "There are no more debates"
                    + " to delete.");
        }
        else
        {
            int result = JOptionPane.showConfirmDialog(null, "Are you sure you "
                                            + "want to delete this record?");
            
            if(result == 0)
            {
                debateList.remove(currentDebateNumber-1);
                writeToFile();
                if(currentDebateNumber > 1 && debateList.size() >= 1)
                {
                    currentDebateNumber--;
                }
                if(currentDebateNumber == 1 && debateList.size() == 1)
                {
                    firstDebateButton.setEnabled(false);
                    previousDebateButton.setEnabled(false);
                    lastDebateButton.setEnabled(false);
                    nextDebateButton.setEnabled(false);
                }
                if(debateList.isEmpty())
                {
                    clearAllFields();
                    debateNumberLabel.setText("0 of 0");
                }
                else
                {
                    populateViewingFields(currentDebateNumber);
                }
                JOptionPane.showMessageDialog(null, "The record has "
                                              + "been deleted.");
            }
        }
    }//GEN-LAST:event_deleteButtonActionPerformed
