    /**
     * Allows the user to view a data about a particular debate session
     * without having to traverse click through every single debate
     * session held before that.
     * @param evt A mouse click on this button.
     */
    private void jumpButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jumpButtonActionPerformed
        currentDebateNumber = Integer.decode(jumpToDebateNumberField.getText());
        if(currentDebateNumber == 1)
        {
            previousDebateButton.setEnabled(false);
            firstDebateButton.setEnabled(false);
            nextDebateButton.setEnabled(true);
            lastDebateButton.setEnabled(true);
        }
        else if(currentDebateNumber == debateList.size())
        {
            nextDebateButton.setEnabled(false);
            lastDebateButton.setEnabled(false);
            previousDebateButton.setEnabled(true);
            firstDebateButton.setEnabled(true);
        }
        else if(currentDebateNumber > 1 && 
                currentDebateNumber < debateList.size())
        {
            nextDebateButton.setEnabled(true);
            lastDebateButton.setEnabled(true);
            previousDebateButton.setEnabled(true);
            firstDebateButton.setEnabled(true);
        }
        populateViewingFields(currentDebateNumber); 
    }//GEN-LAST:event_jumpButtonActionPerformed

