    /**
     * A search of the debate sessions in the file is initiated every time
     * a character is typed into the search field.  The time taken to search
     * increases linearly as a straightforward linear search is used to 
     * traverse the linked list containing data on debate sessions.
     * @param evt The release of a key after a key has been pressed
     * and the focus is within this field.
     */
    private void searchFieldKeyReleased(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_searchFieldKeyReleased
        if(searchField.getText().equals(""))
        {
            debateTableModel.restoreMasterList();
        }
        else
        {
            debateTableModel.search(searchField.getText());
        }
        numberOfResults.setText(debateTableModel.getRowCount() + 
                " search results");
    }//GEN-LAST:event_searchFieldKeyReleased

