    /**
     * The debate being scored is first saved to the object model and
     * appended to the end of the debate text file through the help
     * of the persister.
     * @param evt A mouse click on this button.
     */
    private void writeButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_writeButtonActionPerformed
        if(motionField.getText().equals(""))
        {
            JOptionPane.showMessageDialog(null, "There is no motion title.");
        }
        int decision = JOptionPane.showConfirmDialog(null, "Are you sure "
                + "you want to write the debate session to file now?  "
                + "Only click this once you are done adjudicating the debate.");
        if(decision == 0)
        {
            saveToObjectModel();
            try
            {
                persister.appendToFile(debateInSession);
            }
            catch(IOException e)
            {
                JOptionPane.showMessageDialog(null, "There was an error "
                                              + " writing the file to disk");
            }
            JOptionPane.showMessageDialog(null, "The debate has been written"
                                          + " to file.");
        }
    }//GEN-LAST:event_writeButtonActionPerformed

